INSERT INTO `authors` (`id`, `name`, `alias`, `description`, `created_at`, `updated_at`) VALUES
(1, 'Nguyễn Nhật Ánh', 'nguyen-nhat-anh', '<p>Nguyễn Nhật &Aacute;nh l&agrave; t&ecirc;n v&agrave; cũng l&agrave; b&uacute;t danh của một nh&agrave; văn Việt Nam chuy&ecirc;n viết cho tuổi mới lớn.<br />\r\nThuở nhỏ &ocirc;ng theo học tại c&aacute;c trường Tiểu La, Trần Cao V&acirc;n v&agrave; Phan Chu Trinh. Từ 1973 Nguyễn Nhật &Aacute;nh chuyển v&agrave;o sống tại S&agrave;i G&ograve;n, theo học ng&agrave;nh sư phạm. &Ocirc;ng đ&atilde; từng đi Thanh ni&ecirc;n xung phong, dạy học, l&agrave;m c&ocirc;ng t&aacute;c Đo&agrave;n Thanh ni&ecirc;n Cộng Sản Hồ Ch&iacute; Minh. Từ 1986 đến nay &ocirc;ng l&agrave; ph&oacute;ng vi&ecirc;n nhật b&aacute;o S&agrave;i G&ograve;n Giải Ph&oacute;ng, lần lượt viết về s&acirc;n khấu, phụ tr&aacute;ch mục tiểu phẩm, phụ tr&aacute;ch trang thiếu nhi v&agrave; hiện nay l&agrave; b&igrave;nh luận vi&ecirc;n thể thao tr&ecirc;n b&aacute;o S&agrave;i G&ograve;n Giải Ph&oacute;ng Chủ nhật với b&uacute;t danh Chu Đ&igrave;nh Ngạn. Ngo&agrave;i ra, Nguyễn Nhật &Aacute;nh c&ograve;n c&oacute; những b&uacute;t danh kh&aacute;c như Anh Bồ C&acirc;u, L&ecirc; Duy Cật, Đ&ocirc;ng Phương S&oacute;c, S&oacute;c Phương Đ&ocirc;ng,...</p>', NULL, NULL),
(2, 'Nguyễn Ngọc Tư', 'nguyen-ngoc-tu', '<p><strong>Nguyễn Ngọc Tư</strong>&nbsp;(sinh năm&nbsp;<a href="https://vi.wikipedia.org/wiki/1976">1976</a>&nbsp;tại x&atilde; T&acirc;n Duyệt, huyện&nbsp;<a href="https://vi.wikipedia.org/wiki/%C4%90%E1%BA%A7m_D%C6%A1i">Đầm Dơi</a>, tỉnh&nbsp;<a href="https://vi.wikipedia.org/wiki/C%C3%A0_Mau">C&agrave; Mau</a>) l&agrave; một nữ nh&agrave; văn của&nbsp;<a href="https://vi.wikipedia.org/wiki/H%E1%BB%99i_Nh%C3%A0_v%C4%83n_Vi%E1%BB%87t_Nam">Hội nh&agrave; văn Việt Nam</a>, được biết đến nhiều nhất bởi tập truyện g&acirc;y tranh c&atilde;i mang t&ecirc;n&nbsp;<em><a href="https://vi.wikipedia.org/wiki/C%C3%A1nh_%C4%91%E1%BB%93ng_b%E1%BA%A5t_t%E1%BA%ADn">C&aacute;nh đồng bất tận</a></em>. Tập truyện đ&atilde; nhận được giải thưởng của&nbsp;<a href="https://vi.wikipedia.org/wiki/H%E1%BB%99i_Nh%C3%A0_v%C4%83n_Vi%E1%BB%87t_Nam">Hội nh&agrave; văn Việt Nam</a>&nbsp;năm&nbsp;<a href="https://vi.wikipedia.org/wiki/2006">2006</a>&nbsp;v&agrave; truyện ngắn&nbsp;<em>C&aacute;nh đồng bất tận</em>&nbsp;đ&atilde; được chuyển thể th&agrave;nh&nbsp;<a href="https://vi.wikipedia.org/wiki/C%C3%A1nh_%C4%91%E1%BB%93ng_b%E1%BA%A5t_t%E1%BA%ADn_(phim)">phim điện ảnh c&ugrave;ng t&ecirc;n</a>&nbsp;của đạo diễn bởi Nguyễn Phan Quang B&igrave;nh năm&nbsp;<a href="https://vi.wikipedia.org/wiki/2010">2010</a>. Năm 2018, c&ocirc; được trao Giải thưởng Literaturpreis do Litprom (Hiệp hội quảng b&aacute; văn học ch&acirc;u &Aacute;, ch&acirc;u Phi, Mỹ Latin ở Đức) b&igrave;nh chọn, dựa tr&ecirc;n việc xem x&eacute;t c&aacute;c bản dịch tiếng Đức t&aacute;c phẩm nổi bật của c&aacute;c t&aacute;c giả nữ đương đại ti&ecirc;u biểu trong khu vực. Giải thưởng được trao h&agrave;ng năm nhằm vinh danh c&aacute;c t&aacute;c giả nữ đến từ ch&acirc;u &Aacute;, Phi, Mỹ Latin, C&aacute;c tiểu vương quốc Ả Rập thống nhất (UAE) v&agrave; v&ugrave;ng Caribe.</p>', NULL, NULL),
(14, 'Sái Tuấn', 'sai-tuan', '<p>Bắt đầu viết tiểu thuyết kinh dị từ năm 22 tuổi, c&ugrave;ng năm đ&oacute; S&aacute;i Tuấn được nhận &ldquo;Giải thưởng văn học trẻ &ldquo;C&uacute;p Bertelsman - văn học nh&acirc;n d&acirc;n&rdquo;.&nbsp;<br />\r\n<br />\r\nTh&agrave;nh c&ocirc;ng n&agrave;y đ&atilde; gi&uacute;p S&aacute;i Tuấn trở th&agrave;nh một trong những t&aacute;c giả được bạn đọc tại Trung Quốc v&agrave; tr&ecirc;n to&agrave;n thế giới biết đến trong v&agrave;i năm trở lại đ&acirc;y.&nbsp;</p>', NULL, NULL),
(15, 'J K Rowling', 'j-k-rowling', '<p>Joanne &quot;Jo&quot; Rowling, sinh ng&agrave;y 31 th&aacute;ng 7 năm 1965, b&uacute;t danh l&agrave; J. K. Rowling, v&agrave; Robert Galbraith. Cư ngụ tại thủ đ&ocirc; Edinburgh, Scotland l&agrave; tiểu thuyết gia người Anh, t&aacute;c giả bộ truyện giả tưởng nổi tiếng Harry Potter với b&uacute;t danh J. K. Rowling.</p>', NULL, NULL),
(16, 'Lý Lan', 'ly-lan', '<p>L&yacute; Lan l&agrave; một nữ nh&agrave; văn, nh&agrave; thơ v&agrave; dịch giả tiếng Anh của Việt Nam.</p>', NULL, NULL),
(17, 'Chu Hạo Huy', 'chu-hao-huy', '<p>Chu Hạo Huy tốt nghiệp thạc sĩ ng&agrave;nh Khoa học c&ocirc;ng nghệ trường Đại học Thanh Hoa, l&agrave; nh&agrave; văn chuy&ecirc;n viết tiểu thuyết trinh th&aacute;m, được mọi người y&ecirc;u mến qua việc x&acirc;y dựng n&ecirc;n nh&acirc;n vật &ldquo;Cảnh s&aacute;t La Phi&rdquo; trong bộ s&ecirc;-ri. Hiện nay anh đ&atilde; xuất bản hơn 10 cuốn tiểu thuyết, v&agrave; c&oacute; nhiều t&aacute;c phẩm đ&atilde; được dựng th&agrave;nh phim. T&aacute;c phẩm ti&ecirc;u biểu:Bản th&ocirc;ng b&aacute;o tử vong, Nh&agrave; th&ocirc;i mi&ecirc;n t&agrave; &aacute;c, Khắc họa ch&acirc;n dung tội phạm&hellip;</p>', NULL, NULL),
(18, 'Phan Việt', 'phan-viet', '<p>Phan Việt t&ecirc;n thật l&agrave; Nguyễn Ngọc Hường, sinh năm 1978, tốt nghiệp Đại học Ngoại thương năm 2000, sau đ&oacute; chị sang Mỹ học Cao học Truyền th&ocirc;ng, từ năm 2002 đến nay, chị l&agrave; nghi&ecirc;n cứu sinh chương tr&igrave;nh tiến sĩ về c&ocirc;ng t&aacute;c x&atilde; hội tại ĐH Chicago, Mỹ.</p>\r\n\r\n<p>Trong lớp trẻ tuổi viết văn, nếu Nguyễn Ngọc Tư được coi l&agrave; &ldquo;hiện tượng&rdquo; trong suốt những năm 2000, th&igrave; Phan Việt được đ&aacute;nh gi&aacute; l&agrave; một trong những &ldquo;đ&agrave;i kh&iacute; tượng&rdquo; c&oacute; khả năng &ldquo;ti&ecirc;n b&aacute;o một chiều k&iacute;ch mới cho văn học Việt Nam hiện đại&rdquo;. Bản th&acirc;n Phan Việt tự nhận m&igrave;nh l&agrave; &ldquo;nghi&ecirc;n cứu sinh về c&ocirc;ng t&aacute;c x&atilde; hội nhưng c&oacute; viết văn&rdquo;. Chị c&oacute; vẻ đang quyết liệt để tho&aacute;t khỏi con đường m&agrave; ch&iacute;nh bản th&acirc;n nhận ra: &ldquo;Chấp nhận lửng lơ th&igrave; văn chương của m&igrave;nh chắc chắn l&agrave; bị t&aacute;c động&rdquo;.</p>', NULL, NULL),
(19, 'Michael Part', 'michael-part', NULL, NULL, NULL),
(20, 'Vũ Đình Kính', 'vu-dinh-kinh', NULL, NULL, NULL),
(21, 'Thu Giang', 'thu-giang', '<p>Nguyễn Duy Cần, hiệu Thu Giang, l&agrave; một học giả, nh&agrave; bi&ecirc;n khảo v&agrave; trước t&aacute;c kỳ cựu v&agrave;o bậc nhất Việt Nam giữa thế kỷ 20. &Ocirc;ng nổi bật kh&ocirc;ng chỉ về số lượng t&aacute;c phẩm đồ sộ m&agrave; c&ograve;n ở độ s&acirc;u của học thuật v&agrave; sức ảnh hưởng về mặt tư tưởng đến c&aacute;c tầng lớp thanh ni&ecirc;n tr&iacute; thức của &ocirc;ng.</p>', NULL, NULL),
(22, 'Thùy Minh', 'thuy-minh', NULL, NULL, NULL),
(23, 'Donald J Trump', 'donald-j-trump', NULL, NULL, NULL),
(24, 'Christophe Loviny', 'christophe-loviny', NULL, NULL, NULL),
(25, 'Lưu Hải Sinh', 'luu-hai-sinh', NULL, NULL, NULL),
(26, 'Stephen Hawking', 'stephen-hawking', NULL, NULL, NULL),
(27, 'Cao Chi', 'cao-chi', NULL, NULL, NULL),
(28, 'Phạm Văn Thiều', 'pham-van-thieu', NULL, NULL, NULL),
(29, 'Trịnh Xuân Thuận', 'trinh-xuan-thuan', '<p>Trịnh Xu&acirc;n Thuận l&agrave; nh&agrave; khoa học người Mỹ gốc Việt trong lĩnh vực vật l&yacute; thi&ecirc;n văn, &ocirc;ng đồng thời l&agrave; một nh&agrave; văn đ&atilde; viết nhiều cuốn s&aacute;ch c&oacute; gi&aacute; trị cao về vũ trụ học v&agrave; về những suy nghĩ của bản th&acirc;n trong mối tương quan giữa khoa học v&agrave; niềm tin của &ocirc;ng l&agrave; Phật gi&aacute;o.</p>', NULL, NULL),
(30, 'Minette Walters', 'minette-walters', NULL, NULL, NULL),
(31, 'Sâm Nam', 'sam-nam', NULL, NULL, NULL),
(32, 'Steve Bruce', 'steve-bruce', NULL, NULL, NULL),
(33, 'Uông Xuân Vy', 'uong-xuan-vy', NULL, NULL, NULL),
(34, 'Vi Thảo Nguyên', 'vi-thao-nguyen', NULL, NULL, NULL),
(35, 'Darshani Deane', 'darshani-deane', NULL, NULL, NULL),
(36, 'Đỗ Đình Tấn', 'do-dinh-tan', NULL, NULL, NULL),
(37, 'Trần Hồng', 'tran-hong', NULL, NULL, NULL),
(38, 'Nguyên Phong', 'nguyen-phong', NULL, NULL, NULL),
(39, 'John Green', 'john-green', NULL, NULL, NULL),
(40, 'Anne Frank', 'anne-frank', NULL, NULL, NULL),
(41, 'Tony Buổi Sáng', 'tony-buoi-sang', NULL, NULL, NULL),
(42, 'Mikhail Samarsky', 'mikhail-samarsky', NULL, NULL, NULL),
(43, 'Phạm Bá Thủy', 'pham-ba-thuy', NULL, NULL, NULL),
(44, 'Yoshimoto Yoshio', 'yoshimoto-yoshio', NULL, NULL, NULL),
(45, 'Thu Thảo', 'thu-thao', NULL, NULL, NULL),
(46, 'Humans of Hanoi', 'humans-of-hanoi', NULL, NULL, NULL),
(47, 'Miguel De Cervanties Saavedra', 'miguel-de-cervanties-saavedra', NULL, NULL, NULL),
(48, 'Trương Đắc Vỵ', 'truong-dac-vy', NULL, NULL, NULL),
(49, 'Arthur Conan Doyle', 'arthur-conan-doyle', '<p>Sir Arthur Conan Doyle l&agrave; một nh&agrave; văn người Scotland nổi tiếng với tiểu thuyết trinh th&aacute;m Sherlock Holmes, t&aacute;c phẩm được cho l&agrave; một s&aacute;ng kiến lớn trong lĩnh vực tiểu thuyết trinh th&aacute;m.T&ecirc;n ban đầu của Sherlock Holmes vốn l&agrave; Shelling Ford.</p>', NULL, NULL),
(50, 'Joe Studwell', 'joe-studwell', NULL, NULL, NULL),
(51, 'Trần Cung', 'tran-cung', NULL, NULL, NULL),
(52, 'Charlotte Bronte', 'charlotte-bronte', '<p>Charlotte Bront&euml; l&agrave; một tiểu thuyết gia Anh, l&agrave; chị cả trong 3 chị em nổi tiếng Bront&euml;, t&aacute;c giả của những tiểu thuyết xếp v&agrave;o h&agrave;ng kinh điển của văn học Anh. Charlotte Bront&euml;, người d&ugrave;ng b&uacute;t danh Currer Bell, được biết đến với Jane Eyre, một trong những tiểu thuyết tiếng Anh nổi tiếng nhất mọi thời đại.</p>', NULL, NULL),
(53, 'Henryk Sienkiewicz', 'henryk-sienkiewicz', '<p>Henryk Adam Aleksander Pius Sienkiewicz l&agrave; nh&agrave; văn Ba Lan đoạt giải Nobel Văn học năm 1905. &Ocirc;ng thường k&yacute; dưới c&aacute;c t&aacute;c phẩm của m&igrave;nh bằng b&uacute;t hiệu Litwos.</p>', NULL, NULL),
(54, 'Nhiều Tác Giả', 'nhieu-tac-gia', '<p>Bao gồm nhiều t&aacute;c giả c&ugrave;ng viết s&aacute;ch.</p>', NULL, NULL),
(55, 'Duy Nguyên', 'duy-nguyen', NULL, NULL, NULL),
(56, 'Tư Mã Thiên', 'tu-ma-thien', '<p>Tư M&atilde; Thi&ecirc;n, t&ecirc;n tự l&agrave; Tử Trường, l&agrave; t&aacute;c giả bộ Sử k&yacute;; với bộ sử đ&oacute;, &ocirc;ng được t&ocirc;n l&agrave; Sử th&aacute;nh, một trong Mười vị th&aacute;nh trong lịch sử Trung Quốc. &Ocirc;ng l&agrave;m chức Th&aacute;i sử lệnh rồi Trung thư lệnh, đời nh&agrave; H&aacute;n. Sử k&yacute; l&agrave; c&ocirc;ng tr&igrave;nh sử học lớn nhất của Trung Quốc v&agrave; l&agrave; một trong những quyển sử c&oacute; tiếng nhất của thế giới.</p>', NULL, NULL),
(57, 'Phùng Mộng Long', 'phung-mong-long', '<p>Ph&ugrave;ng Mộng Long, sinh 1574 - mất 1646 sinh v&agrave;o năm thứ 2 thời Vạn Lịch triều Minh v&agrave; mất thời Thuận Trị triều Thanh, l&agrave; t&aacute;c giả của tiểu tuyết nổi tiếng &quot;Đ&ocirc;ng Chu Liệt Quốc&quot;.</p>', NULL, NULL),
(58, 'Nguyễn Đỗ Mục', 'nguyen-do-muc', '<p>Nguyễn Đỗ Mục, tự Trọng Hữu, b&uacute;t hiệu H&igrave; Đ&igrave;nh Nguyễn Văn T&ocirc;i; l&agrave; nh&agrave; văn v&agrave; l&agrave; dịch giả Việt Nam.</p>', NULL, NULL),
(59, 'Victor Hugo', 'victor-hugo', '<p>Victor Hugo l&agrave; một nh&agrave; văn, nh&agrave; thơ, nh&agrave; viết kịch thuộc chủ nghĩa l&atilde;ng mạn nổi tiếng của Ph&aacute;p. &Ocirc;ng cũng đồng thời l&agrave; một nh&agrave; ch&iacute;nh trị, một tr&iacute; thức dấn th&acirc;n ti&ecirc;u biểu của thế kỷ XIX. Victor Hugo chiếm một vị tr&iacute; trang trọng trong lịch sử văn học Ph&aacute;p.</p>', NULL, NULL),
(60, 'Huỳnh Lý', 'huynh-ly', NULL, NULL, NULL),
(62, 'Vũ Đình Liên', 'vu-dinh-lien', NULL, NULL, NULL),
(63, 'Lê Trí Viễn', 'le-tri-vien', NULL, NULL, NULL),
(64, 'Đỗ Đức Hiếu', 'do-duc-hieu', NULL, NULL, NULL),
(65, 'Alexandre Dumas Cha', 'alexandre-dumas-cha', '<p>Alexandre Dumas hay Alexandre Dumas cha để ph&acirc;n biệt với con trai &ocirc;ng, l&agrave; một đại văn h&agrave;o nổi tiếng người Ph&aacute;p. Những t&aacute;c phẩm tiểu thuyết nổi tiếng của &ocirc;ng như l&agrave; Ba ch&agrave;ng l&iacute;nh ngự l&acirc;m, B&aacute; tước Monte Cristo gi&agrave;nh được sự h&acirc;m mộ của độc giả khắp thế giới từ hơn một thế kỷ nay</p>', NULL, NULL),
(66, 'Mai Thế Sang', 'mai-the-sang', NULL, NULL, NULL),
(67, 'Hans Christian Andersen', 'hans-christian-andersen', '<p>Hans Christian Andersen l&agrave; nh&agrave; văn người Đan Mạch chuy&ecirc;n viết truyện cổ t&iacute;ch cho thiếu nhi. Trong tiếng Đan Mạch, t&ecirc;n &ocirc;ng thường được viết l&agrave; H.C. Andersen.</p>', NULL, NULL),
(68, 'Nguyễn Văn Hải', 'nguyen-van-hai', NULL, NULL, NULL),
(69, 'Vũ Minh Toàn', 'vu-minh-toan', NULL, NULL, NULL),
(70, 'La Quán Trung', 'la-quan-trung', '<p>La Qu&aacute;n Trung l&agrave; một nh&agrave; văn Trung Hoa, t&aacute;c giả tiểu thuyết Tam quốc diễn nghĩa nổi tiếng.</p>', NULL, NULL),
(71, 'Grim', 'grim', NULL, NULL, NULL),
(72, 'Tru-cốp-xki', 'tru-cop-xki', NULL, NULL, NULL),
(73, 'Phan Ngọc', 'phan-ngoc', '<p>Phan Ngọc l&agrave; một dịch giả, nh&agrave; ng&ocirc;n ngữ học, nh&agrave; nghi&ecirc;n cứu văn h&oacute;a Việt Nam. &Ocirc;ng được xem l&agrave; nh&agrave; b&aacute;ch khoa cuối c&ugrave;ng của thế hệ tr&iacute; thức Việt Nam được đ&agrave;o tạo dưới thời Ph&aacute;p thuộc; tuy vậy những c&ocirc;ng tr&igrave;nh của &ocirc;ng cũng đ&atilde; g&acirc;y kh&ocirc;ng &iacute;t tranh c&atilde;i về chất lượng học thuật.</p>', NULL, NULL),
(74, 'Ngô Thừa Ân', 'ngo-thua-an', '<p>Ng&ocirc; Thừa &Acirc;n, tự Nhữ Trung, hiệu Xạ Dương sơn nh&acirc;n, l&agrave; một nh&agrave; văn, nh&agrave; thơ Trung Quốc, sống trong thời nh&agrave; Minh.</p>', NULL, NULL),
(75, 'Lev Tolstoy', 'lev-tolstoy', '<p>B&aacute; tước Lev Nikolayevich Tolstoy l&agrave; một tiểu thuyết gia người Nga, nh&agrave; triết học, người theo chủ nghĩa ho&agrave; b&igrave;nh, nh&agrave; cải c&aacute;ch gi&aacute;o dục, người ăn chay, người theo chủ nghĩa v&ocirc; ch&iacute;nh phủ, t&iacute;n hữu Cơ Đốc gi&aacute;o, nh&agrave; tư tưởng đạo đức, v&agrave; l&agrave; một th&agrave;nh vi&ecirc;n c&oacute; ảnh hưởng của gia đ&igrave;nh Tolstoy.</p>', NULL, NULL),
(76, 'Antoine Galland', 'antoine-galland', '<p>Antoine Galland l&agrave; một nh&agrave; phương Đ&ocirc;ng v&agrave; khảo cổ học người Ph&aacute;p, nổi tiếng nhất l&agrave; người phi&ecirc;n dịch đầu ti&ecirc;n của Ch&acirc;u &Acirc;u về Ngh&igrave;n lẻ một đ&ecirc;m m&agrave; &ocirc;ng gọi l&agrave; Les mille et une nuits</p>', NULL, NULL),
(77, 'Ayn Rand', 'ayn-rand', '<p>Ayn Rand l&agrave; một nh&agrave; tiểu thuyết v&agrave; triết gia quốc tịch Mỹ sinh tại Nga. B&agrave; nổi tiếng v&igrave; đ&atilde; ph&aacute;t triển học thuyết Chủ nghĩa kh&aacute;ch quan v&agrave; v&igrave; đ&atilde; viết một số t&aacute;c phẩm như We the Living, The Fountainhead, Atlas Shrugged, For the new Intellectual v&agrave; tiểu thuyết ngắn Anthem.</p>', NULL, NULL),
(78, 'Tô Hoài', 'to-hoai', '<p>T&ocirc; Ho&agrave;i l&agrave; một nh&agrave; văn Việt Nam. Một số t&aacute;c phẩm đề t&agrave;i thiếu nhi của &ocirc;ng được dịch ra ngoại ngữ.</p>', NULL, NULL),
(79, 'Jane Austen', 'jane-austen', '<p>Jane Austen l&agrave; một nữ văn sĩ người Anh, t&aacute;c giả của những t&aacute;c phẩm nổi tiếng như L&yacute; Tr&iacute; v&agrave; T&igrave;nh Cảm, Ki&ecirc;u H&atilde;nh v&agrave; Định Kiến, Trang Vi&ecirc;n Mansfield, Emma, Northanger Abbey, v&agrave; Thuyết Phục.</p>', NULL, NULL),
