INSERT INTO `comments` (`id`, `content`, `score`, `product_id`, `user_id`, `created_at`, `updated_at`, `status`) VALUES
(3, 'Đây là cuốn sách tôi được nghe nhắc đến nhiều nhất từ trước tới giờ.Sau khi đọc xong cuốn này thì tôi cảm thấy đây là cuốn sách nên đọc trong đời, cuốn sách gối đầu giường.Tôi cảm thấy từng bài học trong cuốn sách thật sự rất gần gũi với cuộc sống hàng ngày và tôi có thể áp dụng được nó. Những bài học về cách ứng xử trong cuộc sống, đôi khi tôi đang lãng quên nó đi nhưng lại mang lại ý nghĩa vô cùng to lớn. Nội dung cuốn sách cũng không hề triết lý giáo điều như những cuốn sách khác, vì ngôn từ giản dị cùng với những câu chuyện nhỏ gần gũi trong cuộc sống.Những điều mà Dale Carnegie muốn truyền tải đến người đọc chính là những quan điểm, kinh nghiệm sống của mình trong việc đối nhân xử thế. Chính vì vậy những ai đã từng trải, hoặc biết cách sống tốt với mọi người một cách tự nhiên, mới sinh ra đã tốt thì cuốn sách này chỉ giúp chúng ta củng cố quan điểm của mình. Tuy nhiên, đối với những ai chưa biết mình phải cư xử với mọi người xung quanh như thế nào, hoặc muốn nhìn nhận, đánh giá lại bản thân thì đây là một cuốn sách nên đọc.', 5, 45, 12, NULL, '2018-10-27 06:07:08', 1),
(4, 'Mình bắt đầu sưu tập sách từ cấp 3 và đây là một trong những cuốn sách đầu tiên mình quyết định mua. Thật sự cuốn sách rất cuốn hút. Nhưng cần đọc đi, đọc lại nhiều lần. Thực hành và áp dụng hàng ngày thì những gì viết trong sách mới phát huy công dụng. Thực ra, mỗi ngày chỉ cần dành vài phúc reflect lại hành động của bản thân trong ngày, mở sách ra tra theo mục và đọc lại để so sánh kết quá, cũng như chỉ ra những lỗi sai phạm, cũng như động viên bản thân bởi những điều đã thực hiện được. Highlight những điều quan trọng cần chú ý. Cuốn sách này không chỉ dạy chúng ta những kĩ năng hành xử trong cuộc sống như thế nào cho đẹp lòng người, mà nó cũng như là cuốn sách tu tâm dưỡng tính, hướng thiện con người tránh gây nghiệp chướng, hoặc những xung đột, hiềm khích không đáng có. Nếu mọi người cư xử với nhau bằng lòng tử tế thì xã hội và thế giởi cũng nhờ đó mà trở nên tốt đẹp hơn.', 3, 45, 2, NULL, '2018-10-28 11:33:53', 1),
(5, 'Sách rất hay, cảm ơn ad.', 5, 45, 7, '2018-10-26 09:48:13', '2018-10-27 06:11:15', 1),
(6, 'Sách rất hay.', 5, 44, 5, '2018-10-27 01:49:15', '2018-10-27 06:07:19', 1),
