INSERT INTO `publishers` (`id`, `name`, `alias`, `address`, `email`, `description`, `created_at`, `updated_at`) VALUES
(1, 'First News - Trí Việt', 'first-news-tri-viet', '11H Nguyễn Thị Minh Khai, Phường Bến Nghé, Quận 1, TP. Hồ Chí Minh', 'triviet@firstnews.com.vn', '<p>C&ocirc;ng Ty S&aacute;ch First News&nbsp;được th&agrave;nh lập ng&agrave;y 10/12/1994 với t&ecirc;n gọi Ban Bi&ecirc;n Dịch First News với &yacute; tưởng l&uacute;c bấy giờ l&agrave; &ldquo;Mang đến cho bạn đọc những th&ocirc;ng tin, kiến thức mới nhất v&agrave; hữu &iacute;ch nhất&quot; . First News hiện c&oacute; Nh&agrave; s&aacute;ch Tr&iacute; Việt v&agrave; hơn 150 đại l&yacute; li&ecirc;n kết ph&aacute;t h&agrave;nh tr&ecirc;n to&agrave;n quốc v&agrave; một số đại l&yacute; ph&aacute;t h&agrave;nh ở nước ngo&agrave;i.</p>\r\n\r\n<p>Cho đến nay,&nbsp;<strong>First News&nbsp;</strong>đ&atilde; c&oacute; gần 1000 đầu s&aacute;ch v&agrave; ấn phẩm c&aacute;c loại ph&aacute;t h&agrave;nh với h&agrave;ng triệu bản in được bạn đọc y&ecirc;u th&iacute;ch thuộc c&aacute;c tủ s&aacute;ch: Sống đẹp, Hạt giống T&acirc;m hồn,&nbsp;<em><a href="http://tiki.vn/sach-truyen-tieng-viet/sach-kinh-te.html">S&aacute;ch Kinh doanh</a></em>,&nbsp;<a href="http://tiki.vn/sach-truyen-tieng-viet/sach-kinh-te/doanh-nhan.html">S&aacute;ch doanh nh&acirc;n</a>, Phong thủy, Thường thức Gia đ&igrave;nh, Danh nh&acirc;n, Thiếu nhi, S&aacute;ch Ngoại ngữ, Kiến tr&uacute;c, &Acirc;m nhạc v&agrave;&nbsp;<a href="http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc.html">Truyện tiểu thuyết</a>&nbsp;văn học. Một số tựa s&aacute;ch b&aacute;n chạy nhất của First News:&nbsp;<a href="http://tiki.vn/dac-nhan-tam-3.html">Đắc Nh&acirc;n T&acirc;m</a>, 22 Quy Luật Bất Biến Trong Marketing (<a href="http://tiki.vn/sach-truyen-tieng-viet/sach-kinh-te.html">s&aacute;ch kinh tế</a>), &nbsp;7 Th&oacute;i Quen Để Th&agrave;nh Đạt,&nbsp;Sống Cho Điều &Yacute; Nghĩa Hơn - Nick Vujicic ...</p>', NULL, NULL),
(2, 'Kim Đồng', 'kim-dong', '55 Quang Trung, Hà Nội, Việt Nam', 'info@nxbkimdong.com.vn', '<p>Nh&agrave; xuất bản Kim Đồng trực thuộc Trung ương Đo&agrave;n TNCS Hồ Ch&iacute; Minh l&agrave; Nh&agrave; xuất bản tổng hợp c&oacute; chức năng xuất bản s&aacute;ch v&agrave; văn h&oacute;a phẩm phục vụ thiếu nhi v&agrave; c&aacute;c bậc phụ huynh trong cả nước, quảng b&aacute; v&agrave; giới thiệu văn h&oacute;a Việt Nam ra thế giới.<br />\r\nNh&agrave; xuất bản c&oacute; nhiệm vụ tổ chức bản thảo, bi&ecirc;n soạn, bi&ecirc;n dịch, xuất bản v&agrave; ph&aacute;t h&agrave;nh c&aacute;c xuất bản phẩm c&oacute; nội dung: gi&aacute;o dục truyền thống d&acirc;n tộc, gi&aacute;o dục về tri thức, kiến thức&hellip; tr&ecirc;n c&aacute;c lĩnh vực văn học, nghệ thuật, khoa học kỹ thuật nhằm cung cấp cho c&aacute;c em thiếu nhi cũng như c&aacute;c bậc phụ huynh c&aacute;c kiến thức cần thiết trong cuộc sống, những tinh hoa của tri thức nh&acirc;n loại nhằm g&oacute;p phần gi&aacute;o dục v&agrave; h&igrave;nh th&agrave;nh nh&acirc;n c&aacute;ch thế hệ trẻ.<br />\r\nĐối tượng phục vụ của Nh&agrave; xuất bản l&agrave; c&aacute;c em từ tuổi nh&agrave; trẻ mẫu gi&aacute;o (1 đến 5 tuổi), nhi đồng (6 đến 9 tuổi), thiếu ni&ecirc;n (10 đến 15 tuổi) đến c&aacute;c em tuổi mới lớn (16 đến 18 tuổi) v&agrave; c&aacute;c bậc phụ huynh.</p>', NULL, NULL),
